import { Component,OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { ProductService } from 'src/app/Shared/Services/product.service';

@Component({
  selector: 'app-products-analytics',
  templateUrl: './products-analytics.component.html',
  styleUrls: ['./products-analytics.component.css']
})
export class ProductsAnalyticsComponent {
  productArray:any=[]
  Url:string='http://localhost:8866/'
  particularProductData:any={}
  makeIdPublic:any=''
  updateProductForm:FormGroup| any;
  constructor(
    private ProductService:ProductService,
    private FormBuilder:FormBuilder
    ){}

  ngOnInit():void{this.populateProductArray()}

  populateProductArray(){
    this.ProductService.GetAllProducts().subscribe((ResponseComingFromBackend:any)=>{
      ResponseComingFromBackend.Result.forEach((element:any) => {
        if(element.softDeleteStatus!==1){
          this.productArray.push(element);
        }
      });
    })
  }

  getParticularData(_id:any){
    this.makeIdPublic = _id;
    this.ProductService.GetProductById(_id).subscribe((res:any) => {
      this.particularProductData = res.Result;
      this.updateProductForm = this.FormBuilder.group({
        productName: new FormControl(this.particularProductData?.productName, [Validators.required, Validators.minLength(2), Validators.maxLength(100)]),
        quantity: new FormControl(this.particularProductData?.quantity, [Validators.required, Validators.pattern(/^[0-9]*$/)]),
        price: new FormControl(this.particularProductData?.price, [Validators.required, Validators.pattern(/^[0-9]*$/)]),
        description: new FormControl(this.particularProductData?.description, Validators.required),
        color: new FormControl(this.particularProductData?.color, [Validators.required, Validators.pattern(/^[A-Za-z]+$/)]),
        companyName: new FormControl(this.particularProductData?.companyName, [Validators.required, Validators.pattern(/^[A-Za-z]+$/)]),
        category: new FormControl(this.particularProductData?.category, Validators.required),
        // size: new FormArray([])
      })
    })
  }
}
<div class="container d-flex justify-content-end my-5">
  <button class="btn btn-outline-success">Toggle</button>
</div>
<h1 class="text-center">Welcome To Products Analytics</h1>
<div class="container my-5">
  <table class="table table-hover table-sm table-striped">
    <thead>
      <tr>
        <th class="text-center">#</th>
        <th class="text-center">Product Name</th>
        <th class="text-center">Prduct Image</th>
        <th class="text-center">Price</th>
        <th class="text-center">Status</th>
        <th class="text-center">Status Summary</th>
        <th class="text-center">Company Name</th>
        <th colspan="4" class="text-center">Actions</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let data of productArray; let i=index">
        <th class="text-center">{{i+1}}</th>
        <td class="text-center">{{data.productname}}</td>
        <td class="text-center"><img [src]="Url+data.imageDetails[0].ImageUrl" [alt]="data.productname"
            class="rounded-circle image"></td>
        <td class="text-center">{{data.price}}</td>
        <td class="text-center" [ngClass]="data.status === 1 ? 'text-primary' : 'text-danger'">{{data.status}}</td>
        <td class="text-center" [ngClass]="data.status === 1 ? 'text-primary' : 'text-danger'">{{data.status === 1 ? 'InStock':'Out of Stock'}}</td>
        <td class="text-center">{{data.companyname}}</td>
        <td class="text-center"><i class="fas fa-eye text-info" data-bs-toggle="modal" data-bs-target="#exampleModal"
            ></i></td>
        <td class="text-center"><i class="fas fa-pencil-alt" data-bs-toggle="modal" data-bs-target="#exampleModal1"
            ></i></td>
        <td class="text-center"><button class="btn btn-outline-danger" >delete</button>
        </td>
        <td class="text-center"><button class="btn btn-outline-danger">Hard
            Delete</button></td>
      </tr>
    </tbody>
  </table>
</div>
